public class SwapValue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int i = 10;

		  int j = 7;
		    
		   int x = 0;
		    x = i;
		    i = j;
		    j = x;
		    
		    System.out.println(i);

		    System.out.println(j);

		    // Expected Result , i = 7  and j = 10    
	}

}
